import React from 'react';
import styled from 'styled-components';
import backgroundImage from './assets/banque.png'; 
import Menu from './components/Menu';

const HomePageContainer = styled.div`
  width: 100%;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  text-align: center;
  position: relative;
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 64, 128, 0.7);
  z-index: 1;
`;

const Content = styled.div`
  position: relative;
  z-index: 2;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 48px;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 24px;
  margin-bottom: 40px;
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 18px;
  background-color: #ffffff;
  color: #004080;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #a9c4eb;
  }
`;

const HomePage = () => {
  return (
    <HomePageContainer>
      <Overlay />
      <Content>
        <Title>Bienvenue chez Banqko</Title>
        <Subtitle>Votre partenaire bancaire de confiance</Subtitle>
        <Button>Commencez d√®s maintenant</Button>
      </Content>
    </HomePageContainer>
  );
};

export default HomePage;
